    function generarOpciones(){

    let opciones = [pais_al_azar.name]

        for(let i=0;i<3;i++){
            let opcion = countries[Math.floor(Math.random() * countries.length)].name

            if(opcion != opciones[i]){
                opciones.push(opcion);
            }else{
                i--; //si no esta esto y se repite un pais va generar menos opciones
            }
        }
        /* console.log(opciones) */
        opciones_desordenadas = opciones.sort((a, b) => Math.random() -0.5)
        
    //generador de opciones de respuesta
    let opciones_container = document.getElementById("opciones-respuesta-container")

        opciones_container.innerHTML = ""

        for(let i=0; i<opciones.length; i++){

            opciones_container.innerHTML += `
            
            <button id="opcion${i}" class="btn-opcion">${opciones_desordenadas[i]}</button>
            
            `
        }
    }

